import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

interface CreateEventModalProps {
  show: boolean;
  onClose: () => void;
  onCreate: (eventData: { title: string; start: Date; end: Date; description: string }) => void;
  slotInfo: { start: Date; end: Date } | null;
}

const CreateEventModal: React.FC<CreateEventModalProps> = ({ show, onClose, onCreate, slotInfo }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [start, setStart] = useState<Date | null>( new Date() ||slotInfo?.start || null);
  const [end, setEnd] = useState<Date | null>(slotInfo?.end || null);

console.log('slotInfo',slotInfo)


  const handleSubmit = () => {
    if (!title || !start || !end) {
      alert("Please complete all fields.");
      return;
    }
    onCreate({ title, start, end, description });
    onClose();
  };

  if (!show || !slotInfo) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Create New Event</h2>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter event title"
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter event description"
          />
        </label>
        <label>
          Start Time:
          <DatePicker
            selected={start}
            onChange={(date) => setStart(date || null)}
            showTimeSelect
            dateFormat="Pp"
            placeholderText="Select start time"
          />
        </label>
        <label>
          End Time:
          <DatePicker
            selected={end}
            onChange={(date) => setEnd(date || null)}
            showTimeSelect
            dateFormat="Pp"
            placeholderText="Select end time"
          />
        </label>
        <button onClick={handleSubmit}>Create Event</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default CreateEventModal;
