import React, { useState } from "react";

const LoginPage: React.FC = () => {
  const [showRegistration, setShowRegistration] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    fullName: "",
  });
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    // Perform login (assume an API call here)
    console.log("Logging in...");
    setLoading(false);
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setErrorMessage("Passwords do not match");
      return;
    }
    setLoading(true);
    // Perform registration (assume an API call here)
    console.log("Registering...");
    setLoading(false);
  };

  const toggleForm = () => {
    setShowRegistration(!showRegistration);
    setErrorMessage("");
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="max-w-md w-full bg-white p-8 shadow-lg rounded-lg">
        <h1 className="text-2xl font-bold text-center text-purple-700">
          {showRegistration ? "Register" : "Login"}
        </h1>
        <form
          onSubmit={showRegistration ? handleRegister : handleLogin}
          className="space-y-6 mt-4"
        >
          {showRegistration && (
            <div>
              <label className="text-gray-700">Full Name:</label>
              <input
                type="text"
                value={formData.fullName}
                onChange={(e) =>
                  setFormData({ ...formData, fullName: e.target.value })
                }
                placeholder="Enter full name"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
                required
              />
            </div>
          )}
          <div>
            <label className="text-gray-700">Email:</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) =>
                setFormData({ ...formData, email: e.target.value })
              }
              placeholder="Enter email"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>
          <div>
            <label className="text-gray-700">Password:</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) =>
                setFormData({ ...formData, password: e.target.value })
              }
              placeholder="Enter password"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>
          {showRegistration && (
            <div>
              <label className="text-gray-700">Confirm Password:</label>
              <input
                type="password"
                value={formData.confirmPassword}
                onChange={(e) =>
                  setFormData({ ...formData, confirmPassword: e.target.value })
                }
                placeholder="Re-enter password"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
                required
              />
            </div>
          )}
          {errorMessage && <div className="text-red-500">{errorMessage}</div>}
          <button
            type="submit"
            className="w-full py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
            disabled={loading}
          >
            {loading
              ? "Processing..."
              : showRegistration
              ? "Register"
              : "Login"}
          </button>
        </form>
        <div className="mt-6 text-center">
          <button
            onClick={toggleForm}
            className="text-purple-600 hover:underline"
          >
            {showRegistration
              ? "Already have an account? Login"
              : "Donâ€™t have an account? Register"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
