import React, { useState, useEffect } from 'react';
import { Calendar as BigCalendar, momentLocalizer, Event } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

import { CalendarEvent } from '../types';
import { fetchEvents,createEvent, deleteEvent } from '../api/event';
import Modal from '../components/Modal';

const localizer = momentLocalizer(moment);


const Calendar: React.FC = () => {
    const [events, setEvents] = useState<CalendarEvent[]>([]);
    const [selectedEvent, setSelectedEvent] = useState<CalendarEvent | null>(null); // For selected event
    const [showModal, setShowModal] = useState<boolean>(false);
    const loadEvents = () => {
        fetchEvents().then((data: CalendarEvent[]) => {
            setEvents((prev)=>[...data])
        });
    };

  useEffect(() => {
    loadEvents()
  }, []);

  const handleSelectSlot = async (slotInfo: { start: Date; end: Date }) => {
    console.log('handleSelectSlot','dadsada')

    const title =  window.prompt('Enter Event Title');
    const description = window.prompt('Enter Event Description') || ''; // Default to an empty string if null
    if (title) {
        const event = {
            title,
            start: slotInfo.start,
            end: slotInfo.end,
            description, 
        };
        await createEvent(event)
        console.log('cccccqwewqeq')
       // Update events by appending the new event
       loadEvents()
    }
  };

  // Handle event selection for viewing and deleting
  const handleSelectEvent = (event: CalendarEvent) => {
    setSelectedEvent(event);
    setShowModal(true); // Show modal when an event is clicked
  };

  // Handle event deletion inside modal
  const handleDeleteEvent = async (eventId: string) => {
    await deleteEvent(eventId);
    setShowModal(false); // Close modal after deletion
    loadEvents(); // Reload events after deleting
  };

  console.log('zzzz',events)

  return (
    <div>
        <BigCalendar
            key={events.length+'-'+Date.now()}
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot}
            onSelectEvent={handleSelectEvent}
            style={{ height: 500 }}
        />

      <Modal 
        show={showModal} 
        event={selectedEvent} 
        onClose={() => setShowModal(false)} 
        onDelete={handleDeleteEvent} 
      />
    </div>
  );
};

export default Calendar;
