import React from 'react';
import './Modal.css'; // Custom styling for the modal

interface ModalProps {
  show: boolean;
  event: {
    id: string;
    title: string;
    start: Date;
    end: Date;
    description: string;
  } | null;
  onClose: () => void;
  onDelete: (id: string) => void;
}

const Modal: React.FC<ModalProps> = ({ show, event, onClose, onDelete }) => {

    const handleDelete = () => {
        const confirmDelete = window.confirm('Are you sure you want to delete this event?');
        if (event && confirmDelete) {
          onDelete(event.id);
        }
      };


  if (!show || !event) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{event.title}</h2>
        <p><strong>Start:</strong> {event.start.toString()}</p>
        <p><strong>End:</strong> {event.end.toString()}</p>
        <p><strong>Description:</strong> {event.description}</p>
        <button onClick={() => onDelete(event.id)}>Delete Event</button>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Modal;
