import React, { useState, useEffect } from 'react';
import { Calendar as BigCalendar, momentLocalizer, Event } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

import { CalendarEvent } from '../types';
import { fetchEvents,createEvent, deleteEvent } from '../api/event';

const localizer = momentLocalizer(moment);


const Calendar: React.FC = () => {
  const [events, setEvents] = useState<CalendarEvent[]>([]);

  useEffect(() => {
    fetchEvents().then((data: React.SetStateAction<CalendarEvent[]>) => setEvents(data));
  }, []);

  const handleSelectSlot = (slotInfo: { start: Date; end: Date }) => {
    const title = window.prompt('Enter Event Title');
    const description = window.prompt('Enter Event Description') || ''; // Default to an empty string if null
    if (title) {
      const newEvent = {
        title,
        start: slotInfo.start,
        end: slotInfo.end,
        description, 
      };
      createEvent(newEvent).then(() => fetchEvents().then((data: React.SetStateAction<CalendarEvent[]>) => setEvents(data)));
    }
  };

  const handleDeleteEvent = (eventId: string) => {
    deleteEvent(eventId).then(() => fetchEvents().then((data: React.SetStateAction<CalendarEvent[]>) => setEvents(data)));
  };

  return (
    <div>
      <BigCalendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        selectable
        onSelectSlot={handleSelectSlot}
        onSelectEvent={(event) => handleDeleteEvent(event.id)}
        style={{ height: 500 }}
      />
    </div>
  );
};

export default Calendar;
