import React, { useState, useEffect } from 'react';
import { Calendar as BigCalendar, momentLocalizer, Event } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

import { CalendarEvent } from '../types';
import { fetchEvents,createEvent, deleteEvent } from '../api/event';

const localizer = momentLocalizer(moment);


const Calendar: React.FC = () => {
    const [events, setEvents] = useState<CalendarEvent[]>([]);
    const [isLoading, setIsLoading] = useState<Boolean>(false)
    const [refreshKey, setRefreshKey] = useState<String>();
    const loadEvents = () => {
        fetchEvents().then((data: React.SetStateAction<CalendarEvent[]>) => {
            setEvents(data)

            console.log(data)
        });
    };

  useEffect(() => {
    loadEvents()
  }, []);

  const handleSelectSlot = async (slotInfo: { start: Date; end: Date }) => {
    const title = 'dasadada'// window.prompt('Enter Event Title');
    const description ='ewqeweqewq'// window.prompt('Enter Event Description') || ''; // Default to an empty string if null
    if (title) {
        const newEvent = {
            title,
            start: slotInfo.start,
            end: slotInfo.end,
            description, 
        };
        const newEventList = await createEvent(newEvent)
        console.log('cccccqwewqeq')
        // loadEvents()
        setIsLoading(()=>true)
        setEvents(()=>[])
        setEvents((prev)=>{
            console.log('prev',prev)

            console.log('newEventList',newEventList)
            return newEventList})

        // setIsLoading(()=>false)
    }
  };

  const handleDeleteEvent = (eventId: string) => {
    deleteEvent(eventId).then(() => fetchEvents().then((data: React.SetStateAction<CalendarEvent[]>) => setEvents(data)));
  };

  console.log('zzzz',events)

  return (
    <div>
        <button onClick={()=>{
            console.log('events',events)
        }} >test</button>
      {!isLoading? <BigCalendar
        key={events.length+'-'+Date.now()}
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        selectable
        onSelectSlot={handleSelectSlot}
        onSelectEvent={(event) => handleDeleteEvent(event.id)}
        style={{ height: 500 }}
      />:<></>}
    </div>
  );
};

export default Calendar;
