import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './CreateEventModal.css'; // Import the custom styles

interface CreateEventModalProps {
  show: boolean;
  onClose: () => void;
  onCreate: (eventData: { title: string; start: Date; end: Date; description: string }) => void;
  slotInfo: { start: Date; end: Date } | null;
}

const CreateEventModal: React.FC<CreateEventModalProps> = ({ show, onClose, onCreate, slotInfo }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [start, setStart] = useState<Date | null>(slotInfo?.start || null);
  const [end, setEnd] = useState<Date | null>(slotInfo?.end || null);

  useEffect(() => {
    if (slotInfo?.start) {
      setStart(slotInfo?.start);
    }
    if (slotInfo?.end) {
      setEnd(slotInfo?.end);
    }

    return () => {
      setStart(null);
      setEnd(null);
    };
  }, [slotInfo?.start, slotInfo?.end]);

  const handleSubmit = () => {
    if (!title || !start || !end) {
      alert('Please complete all fields.');
      return;
    }

    if (end <= start) {
      alert('End time cannot be earlier than or equal to start time.');
      return;
    }

    onCreate({ title, start, end, description });
    setStart(null);
    setEnd(null);
    setTitle('');
    setDescription('');

    onClose();
  };

  if (!show || !slotInfo) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Create New Event</h2>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter event title"
            className="input-field"
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter event description"
            className="input-field"
          />
        </label>
        <label>
          Start Time:
          <DatePicker
            selected={start}
            onChange={(date) => setStart(date || null)}
            showTimeSelect
            dateFormat="Pp"
            placeholderText="Select start time"
            className="input-field"
          />
        </label>
        <label>
          End Time:
          <DatePicker
            selected={end}
            onChange={(date) => setEnd(date || null)}
            showTimeSelect
            dateFormat="Pp"
            placeholderText="Select end time"
            className="input-field"
          />
        </label>
        <div className="modal-buttons">
          <button onClick={handleSubmit} className="submit-button">
            Create Event
          </button>
          <button onClick={onClose} className="cancel-button">
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateEventModal;
