import React, { useState, useEffect } from "react";
import { Calendar as BigCalendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";

import { CalendarEvent } from "../types";
import { createEvent, deleteEvent, getEvents } from "../api/event";
import ViewEventModal from "../components/ViewEventModal";
import CreateEventModal from "../components/CreateEventModal";
import { useDispatch } from "react-redux";
import { AppDispatch } from "../store";
import { logoutUser } from "../actions/authActions";

const localizer = momentLocalizer(moment);

// Utility function to generate random color
const getRandomColor = () => {
  const letters = "0123456789ABCDEF";
  let color = "#";
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const CalendarPage: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [userColors, setUserColors] = useState<{ [key: string]: string }>({});
  const [selectedEvent, setSelectedEvent] = useState<CalendarEvent | null>(
    null
  );
  const [showModal, setShowModal] = useState<boolean>(false);
  const [showCreateModal, setShowCreateModal] = useState<boolean>(false);
  const [slotInfo, setSlotInfo] = useState<{ start: Date; end: Date } | null>(
    null
  );

  const loadEvents = async () => {
    try {
      const fetchedEvents = await getEvents();

      // Map over fetched events and convert times to Date objects
      const mappedEvents = fetchedEvents.map((fetchedEvent: CalendarEvent) => ({
        ...fetchedEvent,
        start: new Date(fetchedEvent?.start_time || ""),
        end: new Date(fetchedEvent?.end_time || ""),
      }));

      // Set the events in state
      setEvents(() => mappedEvents);

      // Track users (created_by) and assign them random colors if they don't already have one
      const newUserColors: { [key: string]: string } = {};
      mappedEvents.forEach((event) => {
        const userId = event.created_by;
        if (!userColors[userId]) {
          newUserColors[userId] = getRandomColor(); // Assign a random color to the user
        }
      });

      setUserColors((prevColors) => ({ ...prevColors, ...newUserColors })); // Merge with existing colors
    } catch (error) {
      console.log("Error fetching events", error);
    }
  };

  useEffect(() => {
    loadEvents();
  }, []);

  const handleSelectSlot = (slotInfo: { start: Date; end: Date }) => {
    setSlotInfo(slotInfo);
    setShowCreateModal(true);
  };

  const handleSelectEvent = (event: CalendarEvent) => {
    setSelectedEvent(event);
    setShowModal(true);
  };

  const handleDeleteEvent = async (eventId: string) => {
    try {
      await deleteEvent(eventId);
    } catch (error) {
      console.log("Error deleting event", error);
    }
    setShowModal(false);
    loadEvents(); // Reload events after deletion
  };

  const handleCreateEvent = async (eventData: {
    title: string;
    start: Date;
    end: Date;
    description: string;
  }) => {
    try {
      await createEvent(eventData);
    } catch (error) {
      console.log("Error creating event", error);
    }

    loadEvents(); // Reload events after creation
    setShowCreateModal(false); // Close the modal after creation
  };

  // Function to return custom styles for events based on the user/created_by field
  const eventPropGetter = (event: CalendarEvent) => {
    const backgroundColor = userColors[event.created_by] || "#D3D3D3"; // Default color if no user color found
    return {
      style: {
        backgroundColor,
        borderRadius: "5px",
        opacity: 0.8,
        color: "white",
        border: "0px",
        display: "block",
      },
    };
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="bg-purple-700 text-white w-64 flex-none p-6">
        <h2 className="text-2xl font-semibold mb-6">My Dashboard</h2>
        <nav className="space-y-4">
          <a href="/" className="block py-2 px-4 rounded hover:bg-purple-600">
            Calendar
          </a>
          <a
            href="/events"
            className="block py-2 px-4 rounded hover:bg-purple-600"
          >
            Events
          </a>
          <a
            href="/settings"
            className="block py-2 px-4 rounded hover:bg-purple-600"
          >
            Settings
          </a>
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 bg-gray-100 p-8">
        {/* Top Bar */}
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Calendar</h1>
          <button
            onClick={() => dispatch(logoutUser())}
            className="bg-red-500 text-white py-2 px-4 rounded-lg hover:bg-red-600"
          >
            Logout
          </button>
        </div>

        {/* Calendar */}
        <div className="bg-white p-6 shadow-lg rounded-lg">
          <BigCalendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot}
            onSelectEvent={handleSelectEvent}
            eventPropGetter={eventPropGetter} // Custom event styling based on created_by
            style={{ height: 500 }}
          />
        </div>
      </div>

      {/* Modals */}
      <ViewEventModal
        show={showModal}
        event={selectedEvent}
        onClose={() => setShowModal(false)}
        onDelete={handleDeleteEvent}
      />
      <CreateEventModal
        show={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onCreate={handleCreateEvent}
        slotInfo={slotInfo}
      />
    </div>
  );
};

export default CalendarPage;
