import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login, register } from "../actions/authActions"; // Assuming these actions exist
import { RootState, AppDispatch } from "../store";
import { getOrganizations } from "../api/organization"; // Assuming this API function exists
import { Organization } from "../types";

const LoginPage: React.FC = () => {
  const [showRegistration, setShowRegistration] = useState(false); // Toggle between login and registration
  const [loginForm, setLoginForm] = useState({ email: "", password: "" });
  const [registerForm, setRegisterForm] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    fullName: "",
    organization: "",
  });
  const [errorMessage, setErrorMessage] = useState("");
  const [organizations, setOrganizations] = useState<Organization[]>([]);
  const dispatch = useDispatch<AppDispatch>();
  const loading = useSelector((state: RootState) => state.auth.loading);
  const authError = useSelector((state: RootState) => state.auth.error);

  // Fetch organizations on mount
  useEffect(() => {
    async function fetchOrganizations() {
      try {
        const response = await getOrganizations(); // Call the API to fetch organizations
        setOrganizations(response);
      } catch (error) {
        console.error("Error fetching organizations:", error);
        setErrorMessage("Failed to fetch organizations.");
      }
    }
    fetchOrganizations();
  }, []);

  // Handle Login Form Submission
  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!loginForm.email || !loginForm.password) {
      setErrorMessage("Please fill in all fields.");
      return;
    }
    try {
      // Dispatch login action (assuming your login action returns a promise)
      await dispatch(
        login({ email: loginForm.email, password: loginForm.password })
      );
      setErrorMessage(""); // Clear error message on success
    } catch (error) {
      setErrorMessage("Login failed. Please check your credentials.");
    }
  };

  // Handle Registration Form Submission
  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    if (registerForm.password !== registerForm.confirmPassword) {
      setErrorMessage("Passwords do not match.");
      return;
    }
    if (!registerForm.organization) {
      setErrorMessage("Please select an organization.");
      return;
    }
    try {
      // Dispatch register action (assuming your register action returns a promise)
      await dispatch(
        register({
          email: registerForm.email,
          password: registerForm.password,
          full_name: registerForm.fullName,
          organization: parseInt(registerForm.organization),
        })
      );
      setErrorMessage(""); // Clear error message on success
    } catch (error) {
      setErrorMessage("Registration failed. Please try again.");
    }
  };

  // Toggle between login and registration forms
  const toggleForm = () => {
    setShowRegistration(!showRegistration);
    setErrorMessage(""); // Clear error message when toggling forms
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-purple-500 to-purple-700">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-lg p-8 space-y-6">
        <h1 className="text-3xl font-semibold text-center text-gray-800">
          {showRegistration ? "Register" : "Login"}
        </h1>

        {/* Login Form */}
        {!showRegistration && (
          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <label className="block text-gray-700">Email:</label>
              <input
                type="email"
                value={loginForm.email}
                onChange={(e) =>
                  setLoginForm({ ...loginForm, email: e.target.value })
                }
                placeholder="Enter email"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Password:</label>
              <input
                type="password"
                value={loginForm.password}
                onChange={(e) =>
                  setLoginForm({ ...loginForm, password: e.target.value })
                }
                placeholder="Enter password"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              />
            </div>
            {authError && <p className="text-red-500 text-sm">{authError}</p>}
            <button
              type="submit"
              className="w-full py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
              disabled={loading}
            >
              {loading ? "Logging in..." : "Login"}
            </button>
          </form>
        )}

        {/* Registration Form */}
        {showRegistration && (
          <form onSubmit={handleRegister} className="space-y-4">
            <div>
              <label className="block text-gray-700">Full Name:</label>
              <input
                type="text"
                value={registerForm.fullName}
                onChange={(e) =>
                  setRegisterForm({ ...registerForm, fullName: e.target.value })
                }
                placeholder="Enter full name"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Email:</label>
              <input
                type="email"
                value={registerForm.email}
                onChange={(e) =>
                  setRegisterForm({ ...registerForm, email: e.target.value })
                }
                placeholder="Enter email"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Password:</label>
              <input
                type="password"
                value={registerForm.password}
                onChange={(e) =>
                  setRegisterForm({ ...registerForm, password: e.target.value })
                }
                placeholder="Enter password"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Confirm Password:</label>
              <input
                type="password"
                value={registerForm.confirmPassword}
                onChange={(e) =>
                  setRegisterForm({
                    ...registerForm,
                    confirmPassword: e.target.value,
                  })
                }
                placeholder="Re-enter password"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Organization:</label>
              <select
                value={registerForm.organization}
                onChange={(e) =>
                  setRegisterForm({
                    ...registerForm,
                    organization: e.target.value,
                  })
                }
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400"
                required
              >
                <option value="" disabled>
                  Select organization
                </option>
                {organizations.map((org) => (
                  <option key={org.id} value={org.id}>
                    {org.name}
                  </option>
                ))}
              </select>
            </div>
            {errorMessage && (
              <p className="text-red-500 text-sm">{errorMessage}</p>
            )}
            <button
              type="submit"
              className="w-full py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
              disabled={loading}
            >
              {loading ? "Registering..." : "Register"}
            </button>
          </form>
        )}

        <p
          onClick={toggleForm}
          className="mt-6 text-center text-purple-600 cursor-pointer hover:underline"
        >
          {showRegistration
            ? "Already have an account? Login"
            : "Don’t have an account? Register"}
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
