import React, { useState, useEffect } from 'react';
import './Modal.css'; // Custom styling for the modal

interface CreateEventModalProps {
  show: boolean;
  onClose: () => void;
  onCreate: (eventData: { title: string; start: Date; end: Date; description: string }) => void;
  slotInfo: { start: Date; end: Date } | null;
}

const CreateEventModal: React.FC<CreateEventModalProps> = ({ show, onClose, onCreate, slotInfo }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [start, setStart] = useState<string>('');
  const [end, setEnd] = useState<string>('');

  // Set initial start and end time when modal is shown
  useEffect(() => {
    if (slotInfo) {
      setStart(slotInfo.start.toISOString().slice(0, 16)); // Convert to datetime-local format
      setEnd(slotInfo.end.toISOString().slice(0, 16)); // Convert to datetime-local format
    }
  }, [slotInfo]);

  const handleSubmit = () => {
    if (!title) {
      alert("Please enter a title for the event.");
      return;
    }
    onCreate({
      title,
      start: new Date(start), // Convert string back to Date object
      end: new Date(end),     // Convert string back to Date object
      description
    });
    onClose(); // Close the modal after event creation
  };

  if (!show || !slotInfo) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Create New Event</h2>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter event title"
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter event description"
          />
        </label>
        <label>
          Start Time:
          <input
            type="datetime-local"
            value={start}
            onChange={(e) => setStart(e.target.value)}
          />
        </label>
        <label>
          End Time:
          <input
            type="datetime-local"
            value={end}
            onChange={(e) => setEnd(e.target.value)}
          />
        </label>
        <button onClick={handleSubmit}>Create Event</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default CreateEventModal;
